import Axios from "axios";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Helmet } from "react-helmet-async";
import { useContext, useEffect, useState } from "react";
import { Store } from "../Store";
import { toast } from "react-toastify";
import { getError } from "../utils";
import { apiUrl } from "../components/constant";
import ReCAPTCHA from "react-google-recaptcha";
export default function SigninScreen() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get("redirect");
  const redirect = redirectInUrl ? redirectInUrl : "/";
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [captcha, setCaptcha] = useState(false);

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await Axios.post(`${apiUrl}/api/users/signin`, {
        email,
        password,
      });
      ctxDispatch({ type: "USER_SIGNIN", payload: data });
      localStorage.setItem("userInfo", JSON.stringify(data));
      navigate(redirect || "/");
    } catch (err) {
      toast.error(getError(err));
    }
  };
  const onChangeCaptCha = () => {
    setCaptcha(true);
  };
  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);
  return (
    <Container className="small-container">
      <Helmet>
        <title>Pha Pha Shopping</title>
      </Helmet>
      <h1 className="my-3">Đăng nhập</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <ReCAPTCHA
          sitekey="6LfOHRUgAAAAAJe-pRgsfWCm6S3guTsTrJHMYkQ8"
          onChange={onChangeCaptCha}
        />
        <div className="mb-3">
          {!captcha ? (
            <Button type="submit" disabled>
              Đăng nhập
            </Button>
          ) : (
            <Button type="submit">Đăng nhập</Button>
          )}
        </div>
        <div className="mb-3">
          Bạn chưa có tài khoản?{" "}
          <Link to={`/signup?redirect=${redirect}`}>Đăng ký</Link>
        </div>
      </Form>
    </Container>
  );
}
